@client.event
async def on_ready():
    await client.change_presence(activity=discord.Activity(type=discord.ActivityType.watching, name="for -help"))
    print("Bot is ready.")

async def auto_reset(ctx):
    while True:
        users = await get_data()
        for user in users:
            users[user]["turns"] = 0
            with open("data.json", "w") as f:
                json.dump(users, f)
        await asyncio.sleep(86400)

@client.command()
@commands.has_permissions(administrator=True)
async def start_auto_reset(ctx):
    if resetloop_authors['mention'] == "":
        client.loop.create_task(auto_reset(ctx))
        em = discord.Embed(description="Auto reset loop succesfully started.", colour=discord.Colour.green())
        await ctx.send(embed=em)
    else:
        pass

