async def open_account(user):
    users = await get_data()
    if str(user.id) in users:
        return False
    else:
        users[str(user.id)] = {}
        users[str(user.id)]["points"] = 0
        users[str(user.id)]["turns"] = 0
    with open("data.json", "w") as f:
        json.dump(users, f)
    return True

async def get_data():
    with open("data.json", "r") as f:
        users = json.load(f)
    return users

async def update_points(user, change = 0, mode = "points"):
    users = await get_data()
    users[str(user.id)][mode] += change
    with open("data.json", "w") as f:
        json.dump(users, f)
    bal = users[str(user.id)]["points"], users[str(user.id)]["turns"]
    return bal

